import React,{Component}from 'react';
import Notify from './Notify';
import CartItem from './CartItem';
import {connect} from 'react-redux';
import carts from './../reducers/carts'; 
class Cart extends Component {
  render(){
    let {items} = this.props; 

    return (

 <div className="col-xs-12 col-sm-6 col-md-6 col-lg-6">
        <div className="panel panel-danger"> 
          <div className="panel-heading"><h1 className="panel-title">Your Cart</h1></div> 
          <div className="panel-body">
            <table className="table">
              <thead>
                <tr>
                  <th width="4%">#</th>
                  <th>Pokemon</th>
                  <th width="15%">Price</th>
                  <th width="4%">Quantity</th>
                  <th width="20%">Subtotal</th>
                  <th width="25%">Action</th>
                </tr>
              </thead>
               {this.showBodyElement(items)}
          	<tfoot id="my-cart-footer">
                {/* CART FOOTER */}
                <tr><th colSpan={6}>Empty product in your cart</th></tr>
                <tr>
                  <td colSpan={4}>There are <b>5</b> items in your shopping cart.</td>
                  <td colSpan={2} className="total-price text-left">12 USD</td>
                </tr>
              </tfoot>
            </table>
          </div>
          </div>
               <Notify />
          </div>


)

}
showBodyElement(items){
  let xhml = null; 
  if(items !==null && items.length>0){
    xhml = items.map((item,index)=>{
      return  <CartItem key={index} item={item} index={index}/>
    })
  }
  return <tbody id="my-cart-body">{xhml}</tbody>
}
}
const mapStateToProps = (state) => {
  return {
    items:state.carts
  }
}
export default connect(mapStateToProps,null)(Cart);
