import React,{Component}from 'react';
import {connect} from 'react-redux';
import Helpers from './../libs/Helpers';
import * as configs from './../constants/Config';
import Validate from './../libs/Validate';
class CartItem extends Component {
  constructor(props){
    super(props); 
    this.state={
      value:0
    }
  }
  handleChange(event) {
    this.setState({
      value: event.target.value
    });
  }
  render(){
   let {item,index} = this.props; 
   let {product} = item; 
   console.log(product)
   let quantity =(this.state.value !==0)?this.state.value : item.quantity;
    return (
  <tr>
                  <th scope="row">{index+1}</th>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td><input name="value" value={quantity} onChange={this.handleChange} type="number" min={1}/></td>
                  <td><strong>12 USD</strong></td>
                  <td>
                    <a className="label label-info update-cart-item" href="#" data-product>Update</a>
                    <a className="label label-danger delete-cart-item" href="#" data-product>Delete</a>
                  </td>
                </tr>
)

}
}

export default CartItem;
